mapper.select:



				AND notice_title like concat('%', #{noticeTitle}, '%')


				AND notice_type = #{noticeType}


                AND status = #{status}


				AND create_by like concat('%', #{createBy}, '%')


                AND valid_start_time between #{params.beginValidStartTime} and #{params.endValidStartTime}


		order by create_time desc

mapper.update:
update sys_notice
notice_title = #{noticeTitle}, notice_type = #{noticeType}, dept_id = #{deptId}, notice_content = #{noticeContent}, valid_date = #{validDate}, valid_start_time = #{validStartTime}, valid_end_time = #{validEndTime}, status = #{status}, del_flag = #{delFlag}, create_id = #{createId}, create_by = #{createBy}, create_time = #{createTime}, update_id = #{updateId}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where notice_id = #{noticeId}
