mapper.select:


     and title = #{title}
     and handle_node = #{handleNode}
     and handler = #{handler}
     and handler_name like concat('%', #{handlerName}, '%')
     and handle_time = #{handleTime}
     and todo_id = #{todoId}
     and proc_inst_id = #{procInstId}
     and business_id = #{businessId}
     and task_id = #{taskId}
     and template_id = #{templateId}
     and template_name like concat('%', #{templateName}, '%')
     and type = #{type}
     and create_id = #{createId}
     and update_id = #{updateId}

order by create_time desc

mapper.update:
update t_workflow_done
title = #{title}, handle_node = #{handleNode}, handler = #{handler}, handler_name = #{handlerName}, handle_time = #{handleTime}, todo_id = #{todoId}, proc_inst_id = #{procInstId}, business_id = #{businessId}, task_id = #{taskId}, template_id = #{templateId}, template_name = #{templateName}, template_type = #{templateType}, type = #{type}, create_id = #{createId}, create_by = #{createBy}, create_time = #{createTime}, update_id = #{updateId}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select t2.* from ( select business_id, MAX(handle_time) as max_time from t_workflow_done where proc_inst_id in
#{item}
group by business_id order by max_time desc ) t1 inner join t_workflow_done t2 on t2.business_id = t1.business_id and t2.handle_time = t1.max_time order by t2.handle_time desc
