mapper.select:


    del_flag = '0'
     and title like concat('%', #{title}, '%')
     and cur_node = #{curNode}
     and cur_handler = #{curHandler}
     and cur_handler_name like concat('%', #{curHandlerName}, '%')
     and sender = #{sender}
     and sender_name like concat('%', #{senderName}, '%')
     and send_time = #{sendTime}
     and proc_inst_id = #{procInstId}
     and business_id = #{businessId}
     and task_id = #{taskId}
     and template_id = #{templateId}
     and template_name like concat('%', #{templateName}, '%')
     and type = #{type}
     and type != '2'
     and handle_type = #{handleType}
     and urgency_status = #{urgencyStatus}
     and urge_flag = #{urgeFlag}
     and read_flag = #{readFlag}
     and del_flag = #{delFlag}
     and create_id = #{createId}
     and create_time = #{createTime}
     and update_id = #{updateId}

order by create_time desc

mapper.update:
update t_workflow_todo
title = #{title}, cur_node = #{curNode}, cur_handler = #{curHandler}, cur_handler_name = #{curHandlerName}, sender = #{sender}, sender_name = #{senderName}, send_time = #{sendTime}, proc_inst_id = #{procInstId}, business_id = #{businessId}, task_id = #{taskId}, template_id = #{templateId}, template_name = #{templateName}, template_type = #{templateType}, type = #{type}, handle_type = #{handleType}, urgency_status = #{urgencyStatus}, urge_flag = #{urgeFlag}, read_flag = #{readFlag}, del_flag = #{delFlag}, create_id = #{createId}, create_by = #{createBy}, create_time = #{createTime}, update_id = #{updateId}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

update t_workflow_todo
title = #{title}, del_flag = #{delFlag}, update_id = #{updateId}, update_by = #{updateBy}, update_time = #{updateTime}
where del_flag = 0 and id in
#{item}

update t_workflow_todo
urge_flag = #{urgeFlag}, update_id = #{updateId}, update_by = #{updateBy}, update_time = #{updateTime}
where del_flag = '0' and id in
#{item}

mapper.select:
select count(*) from t_workflow_todo where del_flag = '0' and cur_handler = #{curHandler}


        and type in('0', '1')


        and type in('2')

select t2.* from ( select business_id, MAX(create_time) as create_time from t_workflow_todo where proc_inst_id in
#{item}
group by business_id order by create_time desc ) t1 inner join t_workflow_todo t2 on t2.business_id = t1.business_id and t2.create_time = t1.create_time order by t2.create_time desc
